import React from 'react';
import PropTypes from 'prop-types';
import { firebase } from '../firebase';

export const Checkbox = ({id}) =>{
    const archiveTask = () =>{
        firebase
        .firestore()
        .collection('tasks')
        .doc(id)
        .update({
            archived :true ,
        })

    };
    return(
        <div className="checkbox-holder" data-testid="checkbox-action"></div>
    );
}
